/*ทดสอบภาษาไทย*/SELECT 	t_patient.patient_hn AS HN 	, CASE WHEN (length(t_visit.visit_begin_visit_time)>=10)then to_char(to_date(to_number(substr(t_visit.visit_begin_visit_time,1,4),'9999')-543 || substr(t_visit.visit_begin_visit_time,5,6),'yyyy-mm-dd'),'yyyymmdd')                     ELSE ''   END AS DATEOPD  	,(t_visit.f_visit_type_id || b_report_12files_map_clinic.b_report_12files_std_clinic_id || '1') AS CLINIC  	,(substring(t_diag_icd9.diag_icd9_icd9_number,0,3) 		|| substr(t_diag_icd9.diag_icd9_icd9_number,4)) AS OPER  	,b_employee.employee_number AS DROP  	,t_patient.patient_pid AS PERSON_ID	,t_visit.visit_vn AS SEQFROM t_diag_icd9   	INNER JOIN t_visit ON (t_diag_icd9.diag_icd9_vn = t_visit.t_visit_id)  	INNER JOIN t_patient ON (t_patient.t_patient_id = t_visit.t_patient_id) 	LEFT JOIN b_employee ON (t_diag_icd9.diag_icd9_staff_doctor = b_employee.b_employee_id)  	LEFT JOIN b_report_12files_map_clinic  ON (t_diag_icd9.b_visit_clinic_id = b_report_12files_map_clinic.b_visit_clinic_id)  WHERE t_visit.f_visit_status_id <> '4'  	AND t_visit.f_visit_type_id = '0'	AND t_diag_icd9.diag_icd9_active = '1'  	AND substring(t_visit.visit_begin_visit_time,1,10) >= ?	AND substring(t_visit.visit_begin_visit_time,1,10) <= ?ORDER BY HN