//Source file: E:\\Job\\DrugReportSep\\code\\com\\hospital_os\\control\\PrescribeDB.javapackage com.hosv3.objdb;import com.hosv3.object.*;import com.hospital_os.usecase.connection.*;//import com.hospital_os.utility.*;import com.hospital_os.objdb.*;import java.util.*;import java.sql.*;public class Address2DB extends AddressDB{    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public Address2DB(ConnectionInf db)    {        super(db);    }     public Address2 selectByPK2(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (Address2)v.get(0);    }    public Vector eQuery(String sql) throws Exception    {           Address2 p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new Address2();            p.setObjectId(rs.getString(dbObj.pk_field));            p.fullname = rs.getString(dbObj.fullname);            p.description = rs.getString(dbObj.description);            p.tmbtype = rs.getString(dbObj.tmbtype);            p.ampcode = rs.getString(dbObj.ampcode);            p.cgwcode = rs.getString(dbObj.cgwcode);            p.region = rs.getString(dbObj.region);            list.add(p);        }        rs.close();        return list;    }    // SOmprasong 150110    public Address2 selectChangwatByName(String c_name) throws Exception {        StringBuffer sql = new StringBuffer();        sql.append("select * from ");        sql.append(dbObj.table);        sql.append(" where ");        sql.append(dbObj.description);        sql.append(" = '");        sql.append(c_name);        sql.append("'");        Vector v = eQuery(sql.toString());        if (v.size() == 0) {            return null;        } else {            return (Address2) v.get(0);        }    }    public Address2 selectAmphurByCAddress(Address2 c_addr, String a_name) throws Exception {        StringBuffer sql = new StringBuffer();        sql.append("select * from ");        sql.append(dbObj.table);        sql.append(" where ");        sql.append(dbObj.description);        sql.append(" = '");        sql.append(a_name);        sql.append("'");        sql.append(" and ");        sql.append(dbObj.cgwcode);        sql.append(" = '");        sql.append(c_addr.getCode());        sql.append("'");        Vector v = eQuery(sql.toString());        if (v.size() == 0) {            return null;        } else {            return (Address2) v.get(0);        }    }    public Address2 selectTambolByAAddress(Address2 a_addr, String t_name) throws Exception {        StringBuffer sql = new StringBuffer();        sql.append("select * from ");        sql.append(dbObj.table);        sql.append(" where ");        sql.append(dbObj.description);        sql.append(" = '");        sql.append(t_name);        sql.append("'");        sql.append(" and ");        sql.append(dbObj.cgwcode);        sql.append(" = '");        sql.append(a_addr.cgwcode);        sql.append("'");        sql.append(" and ");        sql.append(dbObj.ampcode);        sql.append(" = '");        sql.append(a_addr.ampcode);        sql.append("'");        Vector v = eQuery(sql.toString());        if (v.size() == 0) {            return null;        } else {            return (Address2) v.get(0);        }    }}