package com.hosv3.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import java.util.*;import java.sql.*;public class AppointmentStatusDB{    public ConnectionInf theConnectionInf;    public ComboFix dbObj;    public String table;    final public String idtable = "907";        /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public AppointmentStatusDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new ComboFix();        initConfig();    }    public boolean initConfig()    {        table = "f_appointment_status";        dbObj.code = "f_appointment_status_id";        dbObj.name = "appointment_status_name";        return true;    }        public Vector selectAll() throws Exception    {        String sql ="select * from " + table + " order by "+        dbObj.name;        return veQuery(sql);    }            public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.code);            p.name = rs.getString(dbObj.name);            list.add(p);        }        rs.close();        return list;    }        }