//Source file: E:\\Job\\DrugReportSep\\code\\com\\hospital_os\\control\\PrescribeDB.javapackage com.hosv3.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.objdb.*;import java.util.*;public class LookupDB extends EmployeeDB{        /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public LookupDB(ConnectionInf db){        super(db);    }        //////////////////////////////////////////////////////////////////////////////    //////////////////////////////////////////////////////////////////////////////    public Vector selectBySpid(String pk) throws Exception    {        String sql="select b_employee.* from b_employee,b_service_point_doctor "        + " where b_employee.b_employee_id"        + " = b_service_point_doctor.service_point_doctor_staff"        + " and b_service_point_doctor.b_service_point_id='" + pk + "'"        + " and employee_active='1'";                return eQuery(sql);    }    public Vector selectBySpAndDoctorName(String pk,String name) throws Exception    {        String sql="select b_employee.* from b_employee,b_service_point_doctor "        + " where b_employee.b_employee_id"        + " = b_service_point_doctor.service_point_doctor_staff"        + " and b_service_point_doctor.b_service_point_id='" + pk + "'"        + " and employee_active='1' and employee_firstname || employee_lastname like '%" + name + "%'";        return eQuery(sql);    }    //////////////////////////////////////////////////////////////////////////////    //////////////////////////////////////////////////////////////////////////////}