//Source file: E:\\Job\\DrugReportSep\\code\\com\\hospital_os\\control\\PrescribeDB.javapackage com.hosv3.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import com.hospital_os.objdb.*;import java.util.*;import java.sql.*;/** *@deprecated henbe unused **/public class OrderItemStatus2DB extends OrderItemStatusDB{    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public OrderItemStatus2DB(ConnectionInf db)    {        super(db);    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(OrderItemStatus o) throws Exception    {        String sql="";        OrderItemStatus p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"	+ dbObj.description        + " ) values ('"        + p.getObjectId()        + "','" + p.description        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }    public int update(OrderItemStatus o) throws Exception    {        String sql="update " + dbObj.table + " set ";        OrderItemStatus p=o;        String field =""        + "', " + dbObj.description + "='" + p.description        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        sql = Gutil.convertSQLToMySQL(sql+field.substring(2),theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }    //////////////////////////////////////////////////////////////////////////////    public int delete(OrderItemStatus o) throws Exception    {         String sql="delete from " + dbObj.table         + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";         return theConnectionInf.eUpdate(sql);    }    //////////////////////////////////////////////////////////////////////////////    public OrderItemStatus selectByPK(String pk) throws Exception    {         String sql="select * from " + dbObj.table         + " where " + dbObj.pk_field         + " = '" + pk + "'";                  Vector v=eQuery(sql);                  if(v.size()==0)             return null;         else            return (OrderItemStatus)v.get(0);    }    //////////////////////////////////////////////////////////////////////////////    /**     *@deprecated henbe unused     **/    public Vector selectAll() throws Exception    {   Vector vc = new Vector();        String sql ="select * from " + dbObj.table + " order by "+        dbObj.description;        vc = veQuery(sql);        if(vc.size()==0)            return null;        else            return vc;            }        //////////////////////////////////////////////////////////////////////////////            //////////////////////////////////////////////////////////////////////////////    public Vector eQuery(String sql) throws Exception    {        OrderItemStatus p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new OrderItemStatus();            p.setObjectId(rs.getString(dbObj.pk_field));            p.description = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }    //////////////////////////////////////////////////////////////////////////////}