package com.hosv3.object;import com.hospital_os.utility.XPersistent;public class GActionAuth extends XPersistent{	static final long serialVersionUID = 0;    public String gui_id="";    public String gui_name="";    public String auth_id="";    public String auth_name="";    public String is_read = "0";    public String is_write = "0";    public String note="";        public String toString(){        String ret = gui_name;        if(isRead()){            ret = ret+ "  R";            if(isWrite()) ret = ret+ "W";        }        return ret;    }    public String getRead(){        return is_read;    }    public boolean isRead(){        if(is_read==null)            return false;        return is_read.equals("1");    }    public boolean isWrite(){        if(is_write==null)            return false;        return is_write.equals("1");    }}