//Source file: C:\\HospitalOS\\src\\com.hosv3.ui\\Splash.javapackage com.hosv3.utility;import java.awt.*;import javax.swing.*;import com.hospital_os.utility.LongTask;/** * <b>Title:</b>        Splash<br> * <b>Description:</b><blockquote>  Splash screen for program hospital-os project</blockquote> * <b>Copyright:</b>    Copyright (c) 2001<br> * <b>Company:</b>      4th Tier<br> * @author       Surachai Thowong, 13/01/2002 * @version      1.0 */public class Splash0 extends JWindow implements Runnable{    public static final long  serialVersionUID = 0;   BorderLayout borderLayout1 = new BorderLayout ();   JLabel jLabelSplash = new JLabel ();   /**    * @roseuid 3C328572015E    */    public Splash0(){        try{ Init();}        catch(Exception e){  e.printStackTrace(Constant.getPrintStream());      }    }    private void Init() throws Exception{        this.getContentPane().setLayout(borderLayout1);         jLabelSplash.setDoubleBuffered(true);        jLabelSplash.setToolTipText(Constant.getTextBundle("กรุณารอสักครู่"));        jLabelSplash.setDisabledIcon(null);        this.getContentPane().add(jLabelSplash,  BorderLayout.CENTER);        /////////        javax.swing.JPanel jPanel1;        jPanel1 = new javax.swing.JPanel();        jPanel1.setLayout(new java.awt.BorderLayout());//new org.netbeans.lib.awtextra.AbsoluteLayout());        /////        javax.swing.JProgressBar progressBar;        LongTask task;        task = new LongTask();        progressBar = new javax.swing.JProgressBar(0, task.getLengthOfTask());        progressBar.setValue(0);        progressBar.setIndeterminate(true);        task.go();        progressBar.setMaximumSize(new java.awt.Dimension(5, 7));        progressBar.setPreferredSize(new java.awt.Dimension(220, 7));        progressBar.setStringPainted(true); //get space for the string        progressBar.setString("");          //but don't paint it        progressBar.setForeground(new Color(255,153,51));        progressBar.setBackground(new Color(255,255,255));        jPanel1.add(progressBar, java.awt.BorderLayout.SOUTH);        //, new org.netbeans.lib.awtextra.AbsoluteConstraints(260, 200, -1, -1));        /////                jLabelSplash.setHorizontalAlignment(javax.swing.SwingConstants.CENTER);        jLabelSplash.setLabelFor(jPanel1);        jPanel1.add(jLabelSplash,java.awt.BorderLayout.CENTER);//, new org.netbeans.lib.awtextra.AbsoluteConstraints(0, 0, -1, -1));        getContentPane().add(jPanel1, java.awt.BorderLayout.CENTER);        Constant.println("Config.SPLASH_FILE=" + System.getProperty("user.dir") + "/" + Config.SPLASH_FILE);        jLabelSplash.setIcon(new javax.swing.ImageIcon(System.getProperty("user.dir") + "/" + Config.SPLASH_FILE));        //jLabelSplash.setIcon(new javax.swing.ImageIcon("/data/JobRep/nhso_hrupload/app/icon/splash.jpg"));        setSize(500, 300);        //Center the window        Dimension screenSize = Toolkit.getDefaultToolkit().getScreenSize();        Dimension frameSize = this.getSize();        if (frameSize.height > screenSize.height){            frameSize.height = screenSize.height;        }        if (frameSize.width > screenSize.width){            frameSize.width = screenSize.width;        }        setLocation((screenSize.width - frameSize.width) / 2            , (screenSize.height - frameSize.height) / 2);        setVisible(true);        Thread t = new Thread(this);        t.start();    }    public static void main(String[] argc){        Splash sp = new Splash();        sp.setVisible(true);    }    public void run() {        try {            String html = "<html><body><h1>henbe test hahahaha ok?</h1></body></html>";            Thread.sleep(3000);            //jLabelSplash.setIcon(new javax.swing.ImageIcon(System.getProperty("user.dir") + "/" + Config.SPLASH_FILE_BG));            jLabelSplash.setIcon(null);            jLabelSplash.setText(html);        } catch (InterruptedException ex) {            ex.printStackTrace();        }    }}