SELECT 	t_visit.visit_vn AS AN  	, (substring(diag_icd10_number,0,4) || substr(diag_icd10_number,5)) AS DIAG  	, t_diag_icd10.f_diag_icd10_type_id AS DXTYPE  	, b_employee.employee_number AS DRDX  FROM 	t_diag_icd10          	INNER JOIN t_visit ON (t_diag_icd10.diag_icd10_vn = t_visit.t_visit_id) 	LEFT JOIN b_employee ON (t_diag_icd10.diag_icd10_staff_doctor = b_employee.b_employee_id) WHERE  t_visit.f_visit_type_id = '1'       	AND t_visit.f_visit_status_id <> '4'     	AND t_diag_icd10.diag_icd10_active = '1'           AND substring(t_visit.visit_staff_doctor_discharge_date_time,1,10) >= ?       AND substring(t_visit.visit_staff_doctor_discharge_date_time,1,10) <= ?order by AN