SELECT  	t_visit.visit_vn AS AN       , visit_refer_in_out_refer_hospital as REFER      , case when f_visit_refer_type_id ='1'        then '2'        else '1'  end as REFERTYPE   FROM       t_visit_refer_in_out	inner join t_visit  on t_visit.t_visit_id = t_visit_refer_in_out.t_visit_id	left join t_patient ON (t_patient.t_patient_id = t_visit.t_patient_id)  WHERE   t_visit.f_visit_status_id <> '4' 	AND visit_refer_in_out_active = '1'	AND t_visit.f_visit_type_id = '1'       AND substring(t_visit.visit_staff_doctor_discharge_date_time,1,10) >= ?       AND substring(t_visit.visit_staff_doctor_discharge_date_time,1,10) <= ?ORDER BY AN