--ทดสอบภาษาไทย ????SELECT 	t_patient.patient_hn AS HN 	, CASE WHEN (length(t_diag_icd9.diag_icd9_start_time)>=10)then to_char(to_date(to_number(substr(t_diag_icd9.diag_icd9_start_time,1,4),'9999')-543 || substr(t_diag_icd9.diag_icd9_start_time,5,6),'yyyy-mm-dd'),'yyyymmdd')                     ELSE ''   END AS DATEDX  	, CASE 		WHEN (b_visit_clinic.visit_clinic_number = '11' )			THEN (t_visit.f_visit_type_id || '01' || '1')  		WHEN (b_visit_clinic.visit_clinic_number = '10' ) 			THEN (t_visit.f_visit_type_id|| '11' || '1') 		WHEN (b_visit_clinic.visit_clinic_number = '16' ) 			THEN (t_visit.f_visit_type_id || '05' || '1') 		WHEN (b_visit_clinic.visit_clinic_number = '17' ) 			THEN (t_visit.f_visit_type_id || '06' || '1') 		WHEN (b_visit_clinic.visit_clinic_number = '18' )  			THEN (t_visit.f_visit_type_id || '07' || '1')  		WHEN (b_visit_clinic.visit_clinic_number = '19' ) 			THEN (t_visit.f_visit_type_id || '09'|| '1')  		WHEN (b_visit_clinic.visit_clinic_number = '22' ) 			THEN (t_visit.f_visit_type_id ||'10' || '1') 		WHEN (b_visit_clinic.visit_clinic_number = '02' 			OR  b_visit_clinic.visit_clinic_number = '03' 			OR  b_visit_clinic.visit_clinic_number = '04' 			OR  b_visit_clinic.visit_clinic_number = '08' )  			THEN (t_visit.f_visit_type_id 				|| b_visit_clinic.visit_clinic_number || '1')  		ELSE (t_visit.f_visit_type_id || '12' || '1') END AS CLINIC  	,(substring(t_diag_icd9.diag_icd9_icd9_number,0,3) 		|| substr(t_diag_icd9.diag_icd9_icd9_number,4)) AS OPER  	,b_employee.employee_number AS DROP  FROM t_diag_icd9   	INNER JOIN t_visit ON (t_diag_icd9.diag_icd9_vn = t_visit.t_visit_id)  	INNER JOIN t_patient ON (t_patient.t_patient_id = t_visit.t_patient_id) 	LEFT JOIN b_employee ON (t_diag_icd9.diag_icd9_staff_doctor = b_employee.b_employee_id)  	LEFT JOIN b_visit_clinic ON (t_diag_icd9.b_visit_clinic_id = b_visit_clinic.b_visit_clinic_id )  WHERE t_visit.f_visit_status_id <> '4'  	AND t_visit.f_visit_type_id = '0'	AND t_diag_icd9.diag_icd9_active = '1'  	AND substring(t_visit.visit_begin_visit_time,1,10) >= ?	AND substring(t_visit.visit_begin_visit_time,1,10) <= ?ORDER BY HN