--ทดสอบภาษาไทยselectvisit_hn as hn,visit_vn as an,	 CASE WHEN (length(t_visit.visit_begin_visit_time)>=10)then to_char(to_date(to_number(substr(t_visit.visit_begin_visit_time,1,4),'9999')-543 || substr(t_visit.visit_begin_visit_time,5,6),'yyyy-mm-dd'),'yyyymmdd')                     ELSE ''   END AS  dateopd ,case when     (substr(b_item.item_number,1,2)='HC' or  substr(b_item.item_number,1,2)='CA' or  substr(b_item.item_number,1,1)='O') then '1'    when     (substr(b_item.item_number,1,2)='IP' or  substr(b_item.item_number,1,2)='01') then '2'    when     (substr(b_item.item_number,1,2)='10' or  substr(b_item.item_number,1,2)='20') then '4'    when     (substr(b_item.item_number,1,3)='ART' or  substr(b_item.item_number,1,1)='Z') then '5'    else '3' end as type,b_item.item_number as code,t_order.order_qty as qty,t_order.order_price as rate,visit_vn as seq from t_order inner join b_item on b_item.b_item_id = t_order.b_item_idinner join t_visit on t_order.t_visit_id = t_visit.t_visit_id inner join t_accident on t_accident.t_visit_id = t_visit.t_visit_id    where substr(visit_begin_visit_time,1,10) > ?and substr(visit_begin_visit_time,1,10) < ?and t_order.f_order_status_id <> '3'and (t_order.f_item_group_id ='4'    or t_order.f_item_group_id ='5')and t_visit.f_visit_status_id <>'4'order by hn