package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class BillingGroupItemDB{    public ConnectionInf theConnectionInf;    public BillingGroupItem dbObj;    final private String idtable = "121";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public BillingGroupItemDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new BillingGroupItem();        initConfig();    }    public BillingGroupItemDB()    {        theConnectionInf = null;        dbObj = new BillingGroupItem();        initConfig();    }    public boolean initConfig()    {        dbObj.table="b_item_billing_subgroup";        dbObj.pk_field="b_item_billing_subgroup_id";        dbObj.billing_group_item_id   ="item_billing_subgroup_number";        dbObj.description   ="item_billing_subgroup_description";        dbObj.billing_group_code   ="f_item_billing_group_id";        dbObj.active ="item_billing_subgroup_active";        /*        dbObj.table = "billing_group_item";        dbObj.pk_field = "key_id";        dbObj.billing_group_item_id = "billing_group_item_id";        dbObj.description = "description";        dbObj.billing_group_code = "billing_group_code";        dbObj.active = "active";         */        return true;    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(BillingGroupItem o) throws Exception    {        BillingGroupItem p=o;        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field        ).append( " ,"	).append( dbObj.billing_group_item_id        ).append( " ,"	).append( dbObj.description        ).append( " ,"	).append( dbObj.billing_group_code        ).append( " ,"	).append( dbObj.active        ).append( " ) values ('"        ).append( p.getObjectId()        ).append( "','" ).append( p.billing_group_item_id        ).append( "','" ).append( p.description        ).append( "','" ).append( p.billing_group_code        ).append( "','" ).append( p.active        ).append( "')");        return theConnectionInf.eUpdate(sql.toString());    }    public int update(BillingGroupItem o) throws Exception    {        BillingGroupItem p=o;        StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "        ). append( dbObj.billing_group_item_id ).append( "='" ).append( p.billing_group_item_id        ).append( "', " ).append( dbObj.description ).append( "='" ).append( p.description        ).append( "', " ).append( dbObj.billing_group_code ).append( "='" ).append( p.billing_group_code        ).append( "', " ).append( dbObj.active ).append( "='" ).append( p.active        ).append( "' where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public int delete(BillingGroupItem o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public BillingGroupItem selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (BillingGroupItem)v.get(0);    }        public BillingGroupItem selectByCode(String code) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.billing_group_item_id        ).append( " = '" ).append( code ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (BillingGroupItem)v.get(0);    }        public Vector selectAllByName(String pk,String active) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where ");        if(pk.trim().length() !=0)        {            sql.append("(" ).append(  dbObj.billing_group_item_id            ).append( " like '%" ).append( pk ).append( "%'" ).append( " or UPPER("            ).append( dbObj.description ).append( ") like UPPER('%" ).append( pk ).append( "%')" ).append( ") and ");                    }        sql.append( dbObj.active ).append( " like '%" ).append( active ).append( "%'" ).append( "order by "        ).append( dbObj.billing_group_item_id);        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }        public Vector selectAll() throws Exception    {        Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table ).append( " order by ").append(        dbObj.description);        vc = veQuery(sql.toString());        if(vc.size()==0)            return null;        else            return vc;    }        public Vector selectBgiAll() throws Exception    {        Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table ).append( " order by ").append(        dbObj.description);        vc = eQuery(sql.toString());        if(vc.size()==0)            return null;        else            return vc;    }            /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        BillingGroupItem p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new BillingGroupItem();            p.setObjectId(rs.getString(dbObj.pk_field));            p.billing_group_item_id = rs.getString(dbObj.billing_group_item_id);            p.description = rs.getString(dbObj.description);            p.billing_group_code = rs.getString(dbObj.billing_group_code);            p.active = rs.getString(dbObj.active);            list.add(p);        }        rs.close();        return list;    }    }