package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class BloodGroupDB{    public ConnectionInf theConnectionInf;    public BloodGroup dbObj;    final private String idtable = "128";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public BloodGroupDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new BloodGroup();        initConfig();    }    public boolean initConfig()    {        dbObj.table="f_patient_blood_group";        dbObj.pk_field="f_patient_blood_group_id";        dbObj.description="patient_blood_group_description";                /*        dbObj.table = "blood_group";        dbObj.pk_field = "bgroup_id";        dbObj.description = "description";         */        return true;    }            /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(BloodGroup o) throws Exception    {        String sql="";        BloodGroup p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"	+ dbObj.description        + " ) values ('"        + p.getObjectId()        + "','" + p.description        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }    public int update(BloodGroup o) throws Exception    {        String sql="update " + dbObj.table + " set ";        BloodGroup p=o;        String field =""        + "', " + dbObj.description + "='" + p.description        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        sql = Gutil.convertSQLToMySQL(sql+field.substring(2),theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int delete(BloodGroup o) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";        return theConnectionInf.eUpdate(sql);    }        public BloodGroup selectByPK(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (BloodGroup)v.get(0);    }        public Vector selectAll() throws Exception    {        String sql ="select * from " + dbObj.table + " order by "+        dbObj.description;        return eQuery(sql);    }                /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        BloodGroup p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new BloodGroup();            p.setObjectId(rs.getString(dbObj.pk_field));            p.description = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }    }