package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class DischargeIpdDB{    public ConnectionInf theConnectionInf;    public DischargeIpd dbObj;    final private String idtable = "145";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public DischargeIpdDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new DischargeIpd();        initConfig();    }    public boolean initConfig()    {        dbObj.table="f_visit_ipd_discharge_status";        dbObj.pk_field="f_visit_ipd_discharge_status_id";        dbObj.description  ="visit_ipd_discharge_status_description";        /*        dbObj.table = "discharge_status";        dbObj.pk_field = "discharge_status_id";        dbObj.description = "description";         */        return true;    }            /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(DischargeIpd o) throws Exception    {        String sql="";        DischargeIpd p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"	+ dbObj.description        + " ) values ('"        + p.getObjectId()        + "','" + p.description        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }    public int update(DischargeIpd o) throws Exception    {        String sql="update " + dbObj.table + " set ";        DischargeIpd p=o;        String field =""        + "', " + dbObj.description + "='" + p.description        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        sql = Gutil.convertSQLToMySQL(sql+field.substring(2),theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int delete(DischargeIpd o) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";        return theConnectionInf.eUpdate(sql);    }                public DischargeIpd selectByPK(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else                                                return (DischargeIpd)v.get(0);    }        /**      * @authen Henbe Modify     * @for normal pattern     *     */    public Vector selectAll() throws Exception    {   Vector vc = new Vector();        String sql ="select * from " + dbObj.table + " order by "+        dbObj.pk_field;        return eQuery(sql);            }                /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }            public Vector eQuery(String sql) throws Exception    {        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            DischargeIpd p = new DischargeIpd();            p.setObjectId(rs.getString(dbObj.pk_field));            p.description = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }    }