package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class DrugInstructionDB{    public ConnectionInf theConnectionInf;    public DrugInstruction dbObj;    final public String idtable = "151";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */        public DrugInstructionDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new DrugInstruction();        initConfig();    }    public DrugInstructionDB()    {        theConnectionInf= null;        dbObj = new DrugInstruction();        initConfig();    }    public boolean initConfig()    {        dbObj.table="b_item_drug_instruction";        dbObj.pk_field="b_item_drug_instruction_id";        dbObj.drug_instruction_id   ="item_drug_instruction_number";        dbObj.description   ="item_drug_instruction_description";        dbObj.active="item_drug_instruction_active";        /*        dbObj.table = "drug_instruction";        dbObj.pk_field = "key_id";        dbObj.drug_instruction_id = "drug_instruction_id";        dbObj.description = "description";        dbObj.active = "active";         */        return true;    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(DrugInstruction o) throws Exception    {        DrugInstruction p=o;        p.description = p.description.replace('\n',' ');        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field        ).append( " ,"	).append( dbObj.drug_instruction_id        ).append( " ,"	).append( dbObj.description        ).append( " ,"	).append( dbObj.active        ).append( " ) values ('"        ).append( p.getObjectId()        ).append( "','" ).append( p.drug_instruction_id        ).append( "','" ).append( p.description        ).append( "','" ).append( p.active        ).append( "')");        return theConnectionInf.eUpdate(sql.toString());    }    public int update(DrugInstruction o) throws Exception    {        DrugInstruction p=o;        p.description = p.description.replace('\n',' ');        StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "        ).append( dbObj.drug_instruction_id ).append( "='" ).append( p.drug_instruction_id        ).append( "', " ).append( dbObj.description ).append( "='" ).append( p.description        ).append( "', " ).append( dbObj.active ).append( "='" ).append( p.active        ).append( "' where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public int delete(DrugInstruction o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public DrugInstruction selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (DrugInstruction)v.get(0);    }        public DrugInstruction selectByCode(String code) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where UPPER(" ).append( dbObj.drug_instruction_id        ).append( ") = UPPER('" ).append( code).append( "') ");                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (DrugInstruction)v.get(0);    }        public Vector selectAllByName(String pk,String active) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where ");        if(pk.trim().length() !=0)        {            sql.append("(" ).append(  dbObj.drug_instruction_id            ).append( " like '" ).append( pk ).append( "'" ).append( " or "            ).append( dbObj.description ).append( " like '" ).append( pk ).append( "'" ).append( ") and ");        }                sql.append( dbObj.active ).append( " = '" ).append( active ).append( "'" ).append( "order by "        ).append( dbObj.drug_instruction_id );                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }            public Vector selectAll() throws Exception    {   Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.active ).append( " = '1'"        ).append( " order by ").append( dbObj.description);        vc = veQuery(sql.toString());        if(vc.size()==0)            return null;        else            return vc;            }            /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        DrugInstruction p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new DrugInstruction();            p.setObjectId(rs.getString(dbObj.pk_field));            p.drug_instruction_id = rs.getString(dbObj.drug_instruction_id);            p.description = rs.getString(dbObj.description);            p.active = rs.getString(dbObj.active);            list.add(p);        }        rs.close();        return list;    }    }