package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class DrugSetDB{    public ConnectionInf theConnectionInf;    public DrugSet dbObj;    public DoseDrugSetDB theDoseDrugSetDB;    final public String idtable = "152";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public DrugSetDB(ConnectionInf db,DoseDrugSetDB ddsdb)    {        theConnectionInf=db;        dbObj = new DrugSet();        initConfig();        setDepDB(ddsdb);    }    public void setDepDB(DoseDrugSetDB ddsdb){        theDoseDrugSetDB = ddsdb;    }    public boolean initConfig()    {        dbObj.table="b_item_set";        dbObj.pk_field="b_item_set_id";        dbObj.drug_set_group_key_id   ="b_item_group_id";        dbObj.item_code ="b_item_id";        /*       dbObj.table = "drug_set";        dbObj.pk_field = "key_id";        dbObj.drug_set_group_key_id = "drug_set_group_key_id";        dbObj.item_code = "item_code";         */        return true;    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(DrugSet o) throws Exception    {        String sql="";        DrugSet p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"	+ dbObj.drug_set_group_key_id        + " ,"	+ dbObj.item_code        + " ) values ('"        + p.getObjectId()        + "','" + p.drug_set_group_key_id        + "','" + p.item_code        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        int ret = theConnectionInf.eUpdate(sql);        if(o.dose_drug_set!=null)        {            o.dose_drug_set.drug_set_key_id = o.getObjectId();            theDoseDrugSetDB.deleteByDsid(o.getObjectId());            ret = ret + theDoseDrugSetDB.insert(o.dose_drug_set);        }        return ret;    }    public int update(DrugSet p) throws Exception    {        String sql="update " + dbObj.table + " set "        + dbObj.drug_set_group_key_id + "='" + p.drug_set_group_key_id + "', "        + dbObj.item_code + "='" + p.item_code        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        int ret = theConnectionInf.eUpdate(sql);        if(p.dose_drug_set!=null)        {            p.dose_drug_set.drug_set_key_id = p.getObjectId();            theDoseDrugSetDB.deleteByDsid(p.getObjectId());            ret = ret + theDoseDrugSetDB.insert(p.dose_drug_set);        }        return ret;    }        public int delete(DrugSet o) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";        return theConnectionInf.eUpdate(sql);    }        public int deleteByGroup(String  pk) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.drug_set_group_key_id + "='" + pk +"'";        return theConnectionInf.eUpdate(sql);    }        public DrugSet selectByPK(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (DrugSet)v.get(0);    }        public Vector selectAll() throws Exception    {        String sql="select * from " + dbObj.table;        Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return v;    }        public Vector selectByGroup(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.drug_set_group_key_id        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return v;    }        public Vector eQuery(String sql) throws Exception    {        DrugSet p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new DrugSet();            p.setObjectId(rs.getString(dbObj.pk_field));            p.drug_set_group_key_id = rs.getString(dbObj.drug_set_group_key_id);            p.item_code = rs.getString(dbObj.item_code);            theDoseDrugSetDB.theConnectionInf = theConnectionInf.getClone();            p.dose_drug_set = theDoseDrugSetDB.selectByKeyDrugSet(p.getObjectId());            list.add(p);        }        rs.close();        return list;    }    }