package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class Group504DB{    public ConnectionInf theConnectionInf;    public Group504 dbObj;        /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public Group504DB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new Group504();        initConfig();    }    public Group504DB()    {        theConnectionInf = null;        dbObj = new Group504();        initConfig();    }    public boolean initConfig()    {        Vector result = new Vector();                dbObj.table="f_group_rp504";        dbObj.pk_field="f_group_rp504_id";        dbObj.group_504_id  ="group_rp504_number";        dbObj.description_th   ="group_rp504_description_th";        dbObj.description_en="group_rp504_description_en";        /*                 dbObj.table = "group504";        dbObj.pk_field = "key_id";        dbObj.group_504_id="group_504_id";        dbObj.description_en = "description_en";        dbObj.description_th= "description_th";         */                return true;    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */        public Group504 selectByPk(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field+ " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (Group504)v.get(0);    }    public Group504 selectByGroup504(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.group_504_id        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (Group504)v.get(0);    }        public Vector selectAll() throws Exception    {        String sql="select * from "+ dbObj.table;        Vector v=veQuery(sql);        if(v.size()==0)            return null;        else            return v;    }            public Vector eQuery(String sql) throws Exception    {        Group504 p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        int i=0;        while(rs.next())        {            p = new Group504();            p.setObjectId(rs.getString(dbObj.pk_field));            p.group_504_id= rs.getString(dbObj.group_504_id);            p.description_en= rs.getString(dbObj.description_en);            p.description_th = rs.getString(dbObj.description_th);            list.add(p);            i++;            if(i>100) break;        }        rs.close();        return list;    }        public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description_th);            list.add(p);        }        rs.close();        return list;    }        }