package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class GroupSurveilDB{    public ConnectionInf theConnectionInf;    public GroupSurveil dbObj;    final public String idtable = "318";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public GroupSurveilDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new GroupSurveil();        initConfig();    }    public GroupSurveilDB()    {        theConnectionInf = null;        dbObj = new GroupSurveil();        initConfig();    }        public boolean initConfig()    {        Vector result = new Vector();                dbObj.table="b_group_surveil";        dbObj.pk_field="b_group_surveil_id";        dbObj.group_surveil_id    ="group_surveil_number";        dbObj.description_th     ="group_surveil_description_th";        dbObj.description_en     ="group_surveil_description_en";        dbObj.active ="group_surveil_active";              return true;    }        /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */        public int insert(GroupSurveil o) throws Exception    {        String sql="";        GroupSurveil p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        +  dbObj.pk_field        + " ,"  +dbObj.group_surveil_id        + " ,"	+ dbObj.description_en        + " ,"	+ dbObj.description_th        + " ,"  + dbObj.active        + " ) values ('"        + p.getObjectId()        + "','" + p.group_surveil_id        + "','" + p.description_en        + "','" + p.description_th        + "','" + p.active        + "')";                sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int update(GroupSurveil o) throws Exception    {        String sql="";        GroupSurveil p=o;        sql="update " + dbObj.table + " set "                + dbObj.group_surveil_id +"='" + p.group_surveil_id + "',"        + dbObj.description_th + "='" + p.description_th + "',"        + dbObj.description_en + "='" + p.description_en + "',"        + dbObj.active + "='" + p.active +"' where "        + dbObj.pk_field + "='" + p.getObjectId() + "'";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int delete(GroupSurveil gc) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.pk_field + "='" + gc.getObjectId() +"'";        return theConnectionInf.eUpdate(sql);    }    /**     *@deprecated henbe bad function name     */    public Vector selectByPk(String pk,String active)throws Exception    {        return selectByNameActive(pk,active);    }    public Vector selectByNameActive(String pk,String active)throws Exception    {        String sql="select * from " + dbObj.table        + " where " ;        if(pk.trim().length() !=0)        {                        sql= sql +"("+ dbObj.group_surveil_id + " like '%" + pk + "%') or ("            + dbObj.description_th  + " like '%" + pk + "%') or ("            + dbObj.description_en  + " like '%" + pk + "%') and ("            + dbObj.active + " = '" + active + "')";        }        else        {            sql= sql + dbObj.active + "='" + active +"'";        }        sql += " order by " + dbObj.group_surveil_id;                Vector v=eQuery(sql);                if(v.size()==0)            return null;        else            return v;    }    /**     *@author henbe     */    public GroupSurveil selectByPK(String pk)throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field + " = '" + pk + "'";        Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (GroupSurveil)v.get(0);    }    /**     *@deprecated henbe bad function name     */        public GroupSurveil selectByKeyId(String pk) throws Exception    {        return selectByPK(pk);    }            public GroupSurveil selectByCode(String code) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.group_surveil_id + " = '" + code + "'";                        Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (GroupSurveil)v.get(0);    }            public Vector selectAll() throws Exception    {        String sql="select * from " + dbObj.table;        Vector v=veQuery(sql);                if(v.size()==0)            return null;        else            return v;    }        public Vector selectActivate() throws Exception    {        String sql="select * from "+ dbObj.table+" where group_surveil_active = '1'";                Vector v=veQuery(sql);                if(v.size()==0)            return null;        else            return v;    }      public Vector selectByCN(String key) throws Exception    {        String sql="select * from " + dbObj.table        + " where UPPER(" + dbObj.group_surveil_id        + ") like UPPER('%" + key+ "%') "        + " or UPPER(" + dbObj.description_en        + ") like UPPER('%" + key+ "%') order by " + dbObj.description_en;                Vector v=veEQuery(sql);        if(v.size()==0)            return null;        else            return v;    }           public Vector selectAllEng() throws Exception    {        String sql="select * from " + dbObj.table;                Vector v=veEQuery(sql);                if(v.size()==0)            return null;        else            return v;    }        public Vector eQuery(String sql) throws Exception    {        GroupSurveil p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        int i=0;        while(rs.next())        {            p = new GroupSurveil();            p.setObjectId(rs.getString(dbObj.pk_field));            p.group_surveil_id= rs.getString(dbObj.group_surveil_id);            p.description_en= rs.getString(dbObj.description_en);            p.description_th = rs.getString(dbObj.description_th);            p.active = rs.getString(dbObj.active);            list.add(p);            i++;            if(i>100) break;        }        rs.close();        return list;    }        public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description_th);            list.add(p);        }        rs.close();        return list;    }        public Vector veEQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description_en);            list.add(p);        }        rs.close();        return list;    }        }