package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class OrderContinueDB{    public ConnectionInf theConnectionInf;    public OrderContinue dbObj;    final public String idtable = "194";        /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */        public OrderContinueDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new OrderContinue();        initConfig();    }    public boolean initConfig()    {        dbObj.table="t_order_continue";        dbObj.pk_field="t_order_continue_id";        dbObj.date_continue   ="order_continue_date_time";        dbObj.user_continue   ="order_continue_staff_continue";        dbObj.date_off   ="order_continue_off_date_time";        dbObj.user_off   ="order_continue_staff_off";        dbObj.visit_id   ="t_visit_id";        dbObj.order_item_id   ="t_order_id";        dbObj.common_name="order_continue_common_name";        dbObj.doctor_set_continue = "order_continue_staff_doctor";        dbObj.doctor_set_off = "order_continue_staff_doctor_set_off";        /*        dbObj.table = "order_continue";        dbObj.pk_field ="order_continue_id";        dbObj.date_continue = "date_continue";        dbObj.user_continue = "user_continue";        dbObj.date_off = "date_off";        dbObj.user_off = "user_off";        dbObj.visit_id = "visit_id";        dbObj.order_item_id = "order_item_id";        dbObj.common_name = "common_name";         */        return true;    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(OrderContinue o) throws Exception    {        String sql="";        OrderContinue p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"  + dbObj.date_continue        + " ,"  + dbObj.user_continue        + " ,"	+ dbObj.date_off        + " ,"	+ dbObj.user_off        + " ,"	+ dbObj.visit_id        + " ,"	+ dbObj.order_item_id        + " ,"	+ dbObj.common_name        + " ,"	+ dbObj.doctor_set_continue        + " ,"	+ dbObj.doctor_set_off        + " ) values ('"        + p.getObjectId()        + "','" + p.date_continue        + "','" + p.user_continue        + "','" + p.date_off        + "','" + p.user_off        + "','" + p.visit_id        + "','" + p.order_item_id        + "','" + p.common_name        + "','" + p.doctor_set_continue        + "','" + p.doctor_set_off        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int update(OrderContinue o) throws Exception    {        String sql="update " + dbObj.table + " set ";        OrderContinue p=o;        String field =""        + "', " + dbObj.date_continue + "='" + p.date_continue        + "', " + dbObj.user_continue + "='" + p.user_continue        + "', " + dbObj.date_off + "='" + p.date_off        + "', " + dbObj.user_off + "='" + p.user_off        + "', " + dbObj.visit_id + "='" + p.visit_id        + "', " + dbObj.order_item_id + "='" + p.order_item_id        + "', " + dbObj.common_name + "='" + p.common_name        + "', " + dbObj.doctor_set_continue + "='" + p.doctor_set_continue        + "', " + dbObj.doctor_set_off + "='" + p.doctor_set_off        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        sql = Gutil.convertSQLToMySQL(sql+field.substring(2),theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int delete(OrderContinue o) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";        return theConnectionInf.eUpdate(sql);    }        public OrderContinue selectByPK(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field        + " = '" + pk + "'";        Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (OrderContinue)v.get(0);    }        public Vector selectByOrderItemId(String orderItemId) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.order_item_id        + " = '" + orderItemId + "'";        Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return v;    }        public Vector selectOCByVId(String visitId) throws Exception    {        String sql="select * from " + dbObj.table        + " where "        + dbObj.visit_id + " = '" + visitId + "'";        return eQuery(sql);    }        public Vector selectAll() throws Exception    {        String sql="select * from " + dbObj.table;        Vector v = eQuery(sql);        if(v.size() == 0)            return null;        else            return v;    }        public Vector eQuery(String sql) throws Exception    {        OrderContinue p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new OrderContinue();            p.setObjectId(rs.getString(dbObj.pk_field));                        p.date_continue = rs.getString(dbObj.date_continue);            p.user_continue = rs.getString(dbObj.user_continue);            p.date_off = rs.getString(dbObj.date_off);            p.user_off = rs.getString(dbObj.user_off);            p.visit_id = rs.getString(dbObj.visit_id);            p.order_item_id = rs.getString(dbObj.order_item_id);            p.common_name = rs.getString(dbObj.common_name);            p.doctor_set_continue = rs.getString(dbObj.doctor_set_continue);            p.doctor_set_off = rs.getString(dbObj.doctor_set_off);            list.add(p);        }        rs.close();        return list;    }        }