package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import com.hospital_os.*;import java.util.*;import java.sql.*;import com.hospital_os.usecase.connection.Persistent;public class OrderItemStatusDB{    public ConnectionInf theConnectionInf;    public OrderItemStatus dbObj;    final public String idtable = "200";            /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public OrderItemStatusDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new OrderItemStatus();        initConfig();    }    public boolean initConfig()    {        dbObj.table="f_order_status";        dbObj.pk_field="f_order_status_id";        dbObj.description="order_status_description";        /*        dbObj.table = "order_item_status";        dbObj.pk_field = "order_item_status_id";        dbObj.description = "description";         */        return true;    }            /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(OrderItemStatus o) throws Exception    {        String sql="";        OrderItemStatus p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"	+ dbObj.description        + " ) values ('"        + p.getObjectId()        + "','" + p.description        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }    public int update(OrderItemStatus o) throws Exception    {        String sql="update " + dbObj.table + " set ";        OrderItemStatus p=o;        String field =""        + "', " + dbObj.description + "='" + p.description        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        sql = Gutil.convertSQLToMySQL(sql+field.substring(2),theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int delete(OrderItemStatus o) throws Exception    {         String sql="delete from " + dbObj.table         + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";         return theConnectionInf.eUpdate(sql);    }        public OrderItemStatus selectByPK(String pk) throws Exception    {                  String sql="select * from " + dbObj.table         + " where " + dbObj.pk_field         + " = '" + pk + "'";                  Vector v=eQuery(sql);                  if(v.size()==0)             return null;         else             return (OrderItemStatus)v.get(0);    }        public Vector selectAll() throws Exception    {   Vector vc = new Vector();        String sql ="select * from " + dbObj.table + " order by "+        dbObj.description;        vc = veQuery(sql);        if(vc.size()==0)            return null;        else            return vc;            }   public Vector selectObjAll() throws Exception    {   Vector vc = new Vector();        String sql ="select * from " + dbObj.table + " order by "+        dbObj.description;        vc = eQuery(sql);        if(vc.size()==0)            return null;        else            return vc;            }                    /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }            public Vector eQuery(String sql) throws Exception    {        OrderItemStatus p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new OrderItemStatus();            p.setObjectId(rs.getString(dbObj.pk_field));            p.description = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }    }