/* * QueueICDDB.java * * Created on 27 ????? 2546, 22:54 ?. */package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;/** * * @author  Administrator */public class QueueICDDB{        public ConnectionInf theConnectionInf;    public QueueICD dbObj;    final public String idtable = "220";    /** Creates a new instance of QueueICDDB */    public QueueICDDB(ConnectionInf db)    {        theConnectionInf = db;        dbObj = new QueueICD();        initConfig();    }    public boolean initConfig()    {        dbObj.table="t_visit_queue_coding";        dbObj.pk_field="t_visit_queue_coding_id";        dbObj.visit_id   ="t_visit_id";        dbObj.last_service   ="b_service_point_id";        dbObj.assign_time   ="assign_date_time";        dbObj.patient_id="t_patient_id";        /*        this.dbObj.table = "queue_icd";        this.dbObj.pk_field = "queue_icd_id";                 this.dbObj.visit_id = "visit_id";                 this.dbObj.last_service = "last_service";        this.dbObj.assign_time = "assign_time";        this.dbObj.patient_id = "patient_id";         */        return true;    }    public int insert(QueueICD o) throws Exception    {                QueueICD p=o;        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field  ).append( " ,"        ).append( dbObj.visit_id ).append( " ,"                ).append( dbObj.last_service ).append( " ,"        ).append( dbObj.assign_time  ).append( " ,"        ).append( dbObj.patient_id  ).append( " "                ).append( " ) values ('"        ).append( p.getObjectId() ).append( "','"        ).append( p.visit_id ).append( "' ,'"                ).append( p.last_service ).append( "' ,'"        ).append( p.assign_time  ).append( "' ,'"        ).append( p.patient_id        ).append( "')");                        return theConnectionInf.eUpdate(sql.toString());    }    public int update(QueueICD o) throws Exception    {        QueueICD p=o;                        StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "        ).append( dbObj.visit_id ).append( "='" ).append( p.visit_id ).append( "', "        ).append( dbObj.last_service ).append( "='" ).append( p.last_service ).append( "', "        ).append( dbObj.assign_time  ).append( "='" ).append( p.assign_time ).append( "', "        ).append( dbObj.patient_id  ).append( "='" ).append( p.patient_id ).append( "' "                ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");                        return theConnectionInf.eUpdate(sql.toString());    }        public int delete(QueueICD o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public int deleteByVisitID(String visit_id) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.visit_id ).append( "='" ).append( visit_id ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public QueueICD selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (QueueICD)v.get(0);    }        public QueueICD selectByVisitID(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.visit_id        ).append( " = '" ).append( pk ).append( "'");                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (QueueICD)v.get(0);    }        public QueueICD selectByPatientID(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.patient_id        ).append( " = '" ).append( pk ).append( "'"        ).append( " order by " ).append( dbObj.assign_time);                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (QueueICD)v.get(0);    }        public Vector selectAll() throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table);                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }        public Vector eQuery(String sql) throws Exception    {        QueueICD p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new QueueICD();            p.setObjectId(rs.getString(dbObj.pk_field));            p.visit_id = rs.getString(dbObj.visit_id);                        p.last_service = rs.getString(dbObj.last_service);            p.assign_time = rs.getString(dbObj.assign_time);            p.patient_id = rs.getString(dbObj.patient_id);            list.add(p);        }        rs.close();        return list;    }        public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.visit_id);            list.add(p);        }        rs.close();        return list;    }    }