package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class ServicePointDB{    public ConnectionInf theConnectionInf;    public ServicePoint dbObj;    final public String idtable = "240";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public ServicePointDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new ServicePoint();        initConfig();    }    public ServicePointDB()    {        theConnectionInf= null;        dbObj = new ServicePoint();        initConfig();    }        public boolean initConfig()    {        dbObj.table="b_service_point";        dbObj.pk_field = "b_service_point_id";        dbObj.service_point_id   ="service_point_number";        dbObj.name   ="service_point_description";        dbObj.service_type_id   ="f_service_group_id";        dbObj.service_sub_type_id   ="f_service_subgroup_id";        dbObj.active ="service_point_active";        /*        dbObj.table = "service_point";        dbObj.pk_field = "key_id";        dbObj.service_point_id = "service_point_id";        dbObj.name = "name";        dbObj.service_type_id = "service_type_id";        dbObj.service_sub_type_id = "service_sub_type_id";        dbObj.active = "active";         */        return true;    }    /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(ServicePoint o) throws Exception    {                ServicePoint p=o;        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field        ).append( " ,"	).append( dbObj.service_point_id        ).append( " ,"	).append( dbObj.name        ).append( " ,"	).append( dbObj.service_type_id        ).append( " ,"	).append( dbObj.service_sub_type_id        ).append( " ,"	).append( dbObj.active        ).append( " ) values ('"        ).append( o.getObjectId()        ).append( "','" ).append( p.service_point_id        ).append( "','" ).append( p.name        ).append( "','" ).append( p.service_type_id        ).append( "','" ).append( p.service_sub_type_id        ).append( "','" ).append( p.active        ).append( "')");                        return theConnectionInf.eUpdate(sql.toString());    }    public int update(ServicePoint o) throws Exception    {;        ServicePoint p=o;                StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "                ).append( dbObj.service_point_id ).append( "='" ).append( p.service_point_id        ).append( "', " ).append( dbObj.name ).append( "='" ).append( p.name        ).append( "', " ).append( dbObj.service_type_id ).append( "='" ).append( p.service_type_id        ).append( "', " ).append( dbObj.service_sub_type_id ).append( "='" ).append( p.service_sub_type_id        ).append( "', " ).append( dbObj.active ).append( "='" ).append( p.active        ).append( "' where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");                return theConnectionInf.eUpdate(sql.toString());    }        public int delete(ServicePoint o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public ServicePoint selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (ServicePoint)v.get(0);    }        public Vector selectAllByName(String pk,String active) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where ");        if(pk.trim().length() !=0)        {            sql.append("(" ).append(  dbObj.service_point_id            ).append( " = '" ).append( pk ).append( "'" ).append( " or UPPER("            ).append( dbObj.name ).append( ") LIKE UPPER('%" ).append( pk ).append( "%')" ).append( ") and ");        }                sql.append( dbObj.active ).append( " = '" ).append( active ).append( "'" ).append( " order by "        ).append( dbObj.service_point_id);                return eQuery(sql.toString());    }        public boolean checkServiceIsTreat(String service_id) throws Exception    {        StringBuffer sql = new StringBuffer( "SELECT count(").append(dbObj.service_type_id).append( ") FROM " ).append( dbObj.table ).append( " WHERE " ).append(        " " ).append( dbObj.service_type_id ).append( "='3' and " ).append(        " " ).append( dbObj.pk_field ).append( "='" ).append( service_id ).append( "'");        ResultSet rs = theConnectionInf.eQuery(sql.toString());        int i =0;        while(rs.next())        {            i = rs.getInt(1);        }        rs.close();        rs = null;        sql =null;        if(i ==0)            return false;        else            return true;            }            public ServicePoint selectByCode(String code) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.service_point_id        ).append( " = '" ).append( code ).append( "'");                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (ServicePoint)v.get(0);    }            public Vector selectAll() throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table).append(  " order by ").append( dbObj.name);        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }        public Vector selectAllWithDiagCode() throws Exception    {        StringBuffer sql = new StringBuffer( "SELECT * FROM " ).append( dbObj.table ).append(                      " WHERE " ).append( dbObj.service_type_id ).append( " <> '7' " ).append(                      " and " ).append( dbObj.active ).append( " = '1' " ).append(                      " order by ").append( dbObj.service_point_id);                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }                public Vector selectAllWithOutXL() throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table )                .append(  " where (").append( dbObj.service_sub_type_id ).append(" = '3' " ).append(        "or ").append( dbObj.service_sub_type_id ).append("= '4') " ).append(        "and ").append( dbObj.service_type_id ).append( " <> '7' " ).append(        "and " ).append( dbObj.active ).append( " = '1' "        ).append(" order by ").append( dbObj.service_point_id);                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }        public Vector eQuery(String sql) throws Exception    {        ServicePoint p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ServicePoint();            p.setObjectId(rs.getString(dbObj.pk_field));            p.service_point_id = rs.getString(dbObj.service_point_id);            p.name = rs.getString(dbObj.name);            p.service_type_id = rs.getString(dbObj.service_type_id);            p.service_sub_type_id = rs.getString(dbObj.service_sub_type_id);            p.active = rs.getString(dbObj.active);            list.add(p);        }        rs.close();        return list;    }    }