package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class ServiceTypeDB{    public ConnectionInf theConnectionInf;    public ServiceType dbObj;    final public String idtable = "243";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public ServiceTypeDB(ConnectionInf c)    {        theConnectionInf=c;        dbObj = new ServiceType();        initConfig();    }    public ServiceTypeDB()    {        theConnectionInf=null;        dbObj = new ServiceType();        initConfig();    }    public boolean initConfig()    {        dbObj.table="f_service_group";        dbObj.pk_field="f_service_group_id";        dbObj.description="service_group_description";        /*        dbObj.table = "service_type";        dbObj.pk_field = "service_type_id_";        dbObj.description = "description";         */        return true;    }            /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(ServiceType o) throws Exception    {        String sql="";        ServiceType p=o;        p.generateOID(idtable);        sql="insert into " + dbObj.table + " ("        + dbObj.pk_field        + " ,"	+ dbObj.description        + " ) values ('"        + p.getObjectId()        + "','" + p.description        + "')";        sql = Gutil.convertSQLToMySQL(sql,theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }    public int update(ServiceType o) throws Exception    {        String sql="update " + dbObj.table + " set ";        ServiceType p=o;        String field =""        + "', " + dbObj.description + "='" + p.description        + "' where " + dbObj.pk_field + "='" + p.getObjectId() +"'";        sql = Gutil.convertSQLToMySQL(sql+field.substring(2),theConnectionInf.gettypeDatabase());        return theConnectionInf.eUpdate(sql);    }        public int delete(ServiceType o) throws Exception    {        String sql="delete from " + dbObj.table        + " where " + dbObj.pk_field + "='" + o.getObjectId() +"'";        return theConnectionInf.eUpdate(sql);    }        public ServiceType selectByPK(String pk) throws Exception    {        String sql="select * from " + dbObj.table        + " where " + dbObj.pk_field        + " = '" + pk + "'";                Vector v=eQuery(sql);        if(v.size()==0)            return null;        else            return (ServiceType)v.get(0);    }        public Vector selectAllByName() throws Exception    {        String sql="select * from " + dbObj.table ;                        sql = sql +  " order by "+ dbObj.description;                Vector v=veQuery(sql);        if(v.size()==0)            return null;        else            return v;    }            public Vector selectAll() throws Exception    {   Vector vc = new Vector();        String sql ="select * from " + dbObj.table + " order by "+        dbObj.description;        vc = veQuery(sql);        if(vc.size()==0)            return null;        else            return vc;            }                public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        ServiceType p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql);        while(rs.next())        {            p = new ServiceType();            p.setObjectId(rs.getString(dbObj.pk_field));            p.description = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }    }