package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class UomDB{    public ConnectionInf theConnectionInf;    public Uom dbObj;    final public String idtable = "252";    /**     * @param ConnectionInf db     * @roseuid 3F65897F0326     */    public UomDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new Uom();        initConfig();    }    public UomDB()    {        theConnectionInf=null;        dbObj = new Uom();        initConfig();    }    public boolean initConfig()    {        dbObj.table="b_item_drug_uom";        dbObj.pk_field="b_item_drug_uom_id";        dbObj.uom_id   ="item_drug_uom_number";        dbObj.description   ="item_drug_uom_description";        dbObj.active ="item_drug_uom_active";        /*        dbObj.table = "uom";        dbObj.pk_field = "key_id";        dbObj.uom_id = "uom_id";        dbObj.description = "description";        dbObj.active = "active";         */        return true;    }        /**     * @param cmd     * @param o     * @return int     * @roseuid 3F6574DE0394     */    public int insert(Uom o) throws Exception    {        Uom p=o;        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field        ).append( " ,"	).append( dbObj.uom_id        ).append( " ,"	).append( dbObj.description        ).append( " ,"	).append( dbObj.active        ).append( " ) values ('"        ).append( p.getObjectId()        ).append( "','" ).append( p.uom_id        ).append( "','" ).append( p.description        ).append( "','" ).append( p.active        ).append( "')");        return theConnectionInf.eUpdate(sql.toString());    }    public int update(Uom o) throws Exception    {        Uom p=o;        StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "        ).append( dbObj.uom_id ).append( "='" ).append( p.uom_id        ).append( "', " ).append( dbObj.description ).append( "='" ).append( p.description        ).append( "', " ).append( dbObj.active ).append( "='" ).append( p.active        ).append( "' where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public int delete(Uom o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public Uom selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (Uom)v.get(0);    }            public Uom selectByCode(String code) throws Exception        {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.uom_id        ).append( " = '" ).append( code ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (Uom)v.get(0);    }            public Vector selectAllByName(String pk,String active) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where ");        if(pk.trim().length() !=0)        {            sql.append("(" ).append(  dbObj.uom_id            ).append( " ilike '%" ).append( pk ).append( "%'" ).append( " or "            ).append( dbObj.description  ).append( " ilike '%" ).append( pk ).append( "%'" ).append( ") and ");        }                sql.append( dbObj.active ).append( " = '" ).append( active ).append( "'" ).append( " order by "        ).append( dbObj.uom_id);        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }            public Vector selectAll() throws Exception    {        Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.active ).append( " = '1'"        ).append( " order by ").append( dbObj.description);        vc = veQuery(sql.toString());        if(vc.size()==0)            return null;        else            return vc;    }            /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        Uom p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new Uom();            p.setObjectId(rs.getString(dbObj.pk_field));            p.uom_id = rs.getString(dbObj.uom_id);            p.description = rs.getString(dbObj.description);            p.active = rs.getString(dbObj.active);            list.add(p);        }        rs.close();        return list;    }    }