package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class WardDB{    public ConnectionInf theConnectionInf;    public Ward dbObj;    final public String idtable = "262";        public WardDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new Ward();        initConfig();    }    public WardDB()    {        theConnectionInf=null;        dbObj = new Ward();        initConfig();    }    public boolean initConfig()    {        dbObj.table="b_visit_ward";        dbObj.pk_field="b_visit_ward_id";        dbObj.ward_id   ="visit_ward_number";        dbObj.description   ="visit_ward_description";        dbObj.active="visit_ward_active";        /*        this.dbObj.table = "ward";        dbObj.pk_field = "key_id";        dbObj.ward_id = "ward_id";        dbObj.description = "description";        dbObj.active = "active";         */        return true;    }    public int insert(Ward o) throws Exception    {        Ward p=o;        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field        ).append( " ,"	).append( dbObj.ward_id        ).append( " ,"	).append( dbObj.description        ).append( " ,"	).append( dbObj.active        ).append( " ) values ('"        ).append( p.getObjectId()        ).append( "','" ).append( p.ward_id        ).append( "','" ).append( p.description        ).append( "','" ).append( p.active        ).append( "')");                        return theConnectionInf.eUpdate(sql.toString());    }    public int update(Ward o) throws Exception    {        Ward p=o;        StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "        ).append( dbObj.ward_id ).append( "='" ).append( p.ward_id        ).append( "', " ).append( dbObj.description ).append( "='" ).append( p.description        ).append( "', " ).append( dbObj.active ).append( "='" ).append( p.active        ).append( "' where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public int delete(Ward o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public Ward selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (Ward)v.get(0);    }        public Vector selectAllByName(String pk,String active) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where ");        if(pk.trim().length() !=0)        {            sql.append("(" ).append(  dbObj.ward_id            ).append( " like '" ).append( pk ).append( "'" ).append( " or "            ).append( dbObj.description  ).append( " like '" ).append( pk ).append( "'" ).append( ") and ");        }        sql.append( dbObj.active ).append( " = '" ).append( active ).append( "'" ).append( " order by "        ).append( dbObj.ward_id);        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }            public Ward selectByCode(String code) throws Exception    {        Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.ward_id        ).append( " = '" ).append( code ).append("'");        vc =eQuery(sql.toString());        if(vc.size()==0)            return null;        else            return (Ward)vc.get(0);                    }            public Vector selectAll() throws Exception    {   Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.active ).append( " = '1'"        ).append( " order by ").append(dbObj.description);        vc = veQuery(sql.toString());        if(vc.size()==0)            return null;        else            return vc;    }        /**     *@deprecated henbe unused     *     */    public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        Ward p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new Ward();            p.setObjectId(rs.getString(dbObj.pk_field));            p.ward_id = rs.getString(dbObj.ward_id);            p.description = rs.getString(dbObj.description);            p.active = rs.getString(dbObj.active);            list.add(p);        }        rs.close();        return list;    }    }