package com.hospital_os.objdb;import com.hospital_os.usecase.connection.*;import com.hospital_os.utility.*;import com.hospital_os.object.*;import java.util.*;import java.sql.*;public class XRayLeteralDB{    public ConnectionInf theConnectionInf;    public XRayLeteral dbObj;    final public String idtable = "264";        public XRayLeteralDB(ConnectionInf db)    {        theConnectionInf=db;        dbObj = new XRayLeteral();        initConfig();    }    public XRayLeteralDB()    {        theConnectionInf=null;        dbObj = new XRayLeteral();        initConfig();    }    public boolean initConfig()    {        dbObj.table="b_xray_side";        dbObj.pk_field="b_xray_side_id";        dbObj.xray_leteral_id   ="xray_side_number";        dbObj.description   ="xray_side_description";        dbObj.active ="xray_side_active";        /*        this.dbObj.table = "xray_leteral";        dbObj.pk_field = "key_id";        dbObj.xray_leteral_id = "xray_leteral_id";        dbObj.description = "description";        dbObj.active = "active";         */        return true;    }    public int insert(XRayLeteral o) throws Exception    {                XRayLeteral p=o;        p.generateOID(idtable);        StringBuffer sql = new StringBuffer("insert into " ).append( dbObj.table ).append( " ("        ).append( dbObj.pk_field        ).append( " ,"	).append( dbObj.xray_leteral_id        ).append( " ,"	).append( dbObj.description        ).append( " ,"	).append( dbObj.active        ).append( " ) values ('"        ).append( p.getObjectId()        ).append( "','" ).append( p.xray_leteral_id        ).append( "','" ).append( p.description        ).append( "','" ).append( p.active        ).append( "')");                return theConnectionInf.eUpdate(sql.toString());    }    public int update(XRayLeteral o) throws Exception    {;        XRayLeteral p=o;                StringBuffer sql = new StringBuffer("update " ).append( dbObj.table ).append( " set "                ).append( dbObj.xray_leteral_id ).append( "='" ).append( p.xray_leteral_id        ).append( "', " ).append( dbObj.description ).append( "='" ).append( p.description        ).append( "', " ).append( dbObj.active ).append( "='" ).append( p.active        ).append( "' where " ).append( dbObj.pk_field ).append( "='" ).append( p.getObjectId() ).append("'");                return theConnectionInf.eUpdate(sql.toString());    }        public int delete(XRayLeteral o) throws Exception    {        StringBuffer sql = new StringBuffer("delete from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field ).append( "='" ).append( o.getObjectId() ).append("'");        return theConnectionInf.eUpdate(sql.toString());    }        public XRayLeteral selectByPK(String pk) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.pk_field        ).append( " = '" ).append( pk ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (XRayLeteral)v.get(0);    }        public XRayLeteral selectByCode(String code) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where " ).append( dbObj.xray_leteral_id        ).append( " = '" ).append( code ).append( "'");        Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return (XRayLeteral)v.get(0);    }        public Vector selectAllByName(String pk,String active) throws Exception    {        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " where ");        if(pk.trim().length() !=0)        {            sql.append("(" ).append(  dbObj.xray_leteral_id            ).append( " like '%" ).append( pk ).append( "%'" ).append( " or UPPER("            ).append( dbObj.description  ).append( ") like UPPER('%" ).append( pk ).append( "%')" ).append( ") and ");        }        sql.append( dbObj.active ).append( " = '" ).append( active ).append( "'" ).append( " order by "        ).append( dbObj.xray_leteral_id);                Vector v=eQuery(sql.toString());        if(v.size()==0)            return null;        else            return v;    }        public Vector selectAll() throws Exception    {   Vector vc = new Vector();        StringBuffer sql = new StringBuffer("select * from " ).append( dbObj.table        ).append( " Where " ).append( dbObj.active ).append( " = '1'"        ).append( " order by ").append( dbObj.description) ;        vc = veQuery(sql.toString());        if(vc.size()==0)            return null;        else            return vc;    }        public Vector veQuery(String sql) throws Exception    {        ComboFix p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new ComboFix();            p.code = rs.getString(dbObj.pk_field);            p.name = rs.getString(dbObj.description);            list.add(p);        }        rs.close();        return list;    }        public Vector eQuery(String sql) throws Exception    {        XRayLeteral p;        Vector list = new Vector();        ResultSet rs = theConnectionInf.eQuery(sql.toString());        while(rs.next())        {            p = new XRayLeteral();            p.setObjectId(rs.getString(dbObj.pk_field));            p.xray_leteral_id = rs.getString(dbObj.xray_leteral_id);            p.description = rs.getString(dbObj.description);            p.active = rs.getString(dbObj.active);            list.add(p);        }        rs.close();        return list;    }    }