/* * SecureMgr.java * * Created on 3 กรกฎาคม 2545, 18:40 น. */package com.hospital_os.utility;/** * * @author  neng */public class Secure{    /** Creates a new instance of SecureMgr */    public Secure()    {    }     /** เข้ารหัสข้อมูล */    public static String encode(String dat)   {        char[] text = dat.toCharArray();        char[] crypt = new char[text.length];        for(int i=0; i<text.length; i++)        {                     crypt[text.length-i-1] = text[i];        }        for(int i=0; i<crypt.length; i++)        {                       int letter = crypt[i];            letter+=5;                      crypt[i] = (char)letter;        }        return new String(crypt);    }   /** ถอดรหัสข้อมูล */    public static String decode(String dat)    {      if(dat!=null)      {        char[] text = dat.toCharArray();        char[] crypt = new char[text.length];        for(int i=0; i<text.length; i++)        {                        crypt[text.length-i-1] = text[i];        }        for(int i=0; i<crypt.length; i++)        {              int letter = crypt[i];            letter-=5;                      crypt[i] = (char)letter;        }        return new String(crypt);      }      return null;    }      public static void main(String args[])    {       String dat = "กขคง";        Constant.println(dat);        Constant.println(encode(dat));        Constant.println(decode(encode(dat)));    }    }